import React, { useState } from 'react';
import Clear from "./image/Clear.jpg";
import Clouds from "./image/Clouds.jpg";
import Drizzle from "./image/Drizzle.jpg";
import Mist from "./image/Mist.jpg";
import Rain from "./image/Rain.jpg";
import temp from "temp.jpg";
const api = {
  key: "3b5cff616604c3fac74ed40311ad2ce2",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App(){
  const[query, setQuery] = useState('');
  const[data, setdata] = useState({});

  const search = evt =>{
    if(evt.key === "Enter"){
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
      .then(res => res.json())
      .then(result =>{ 
        setdata(result);
        setQuery('');
        console.log(data.weather);
      });
  }
}

  const dateBuilder = (d) =>{
    let months = ["Styczeń", "Luty", "Marzec", "Kwiecień", "Maj", "Czerwiec", "Lipiec", "Sierpień", "Wrzesień", "Październik", "Listopad", "Grudzień"];
    let days = ["Niedziela", "Poniedziałek", "Wtorek", "Środa", "Czwartek", "Piątek", "Sobota"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }  

  return(
    <div className="app" style={{backgroundImage: `url('${data.main}.jpg')`,
    backgroundSize: "cover",
    backgroundPosition: "bottom"
    }}>
      <main>
        <div className="search-box">
          <input type="text"
          className="search-bar"
          placeholder="Wpisz miasto"
          onChange={e => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
          />
        </div>
        {(typeof data.main != "undefined") ? (
          <div>
            <div className="location-box">
              <div className="location">{data.name}, {data.sys.country}</div>
              <div className="date">{dateBuilder(new Date())}</div>
            </div>
            <div className="weather-box">
              <div className="info">
                <div className='temp'>
              <img src='temp.jpg' style={{height: "20px"}}></img>{Math.round(data.main.temp)}°C<br></br></div>

              <div className='makstemp'>
              {Math.round(data.main.temp_max)}°C<br></br></div>

              <div className='wind'>
              {data.wind.speed}m/s</div>
              </div>
            <div className="weather">{data.weather[0].main}</div>
            </div>
          </div>
        ) :('')}
      </main>
    </div>
  )
}

export default App;
