import React, { Component } from 'react';
import './App.css';
import Form from './Form'
import Result from './Result'

class App extends Component{

state = {
  value: '',
  date: '',
  city: '',
  sunrise: '',
  sunset: '',
  temp: '',
  pressure: '',
  wind: '',
  err: '',
}

handleInputChange = (e) => {
  this.setState({
    value: e.target.value 
  })
}

handleCitySubmit = (e) => {
  e.preventDefault()
  console.log("dziala")
  const API = `https://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&APPID=3b5cff616604c3fac74ed40311ad2ce2units=metric`;

  fetch(API)
  .then(response => console.log(response))
  .catch(err => console.log(err))

}

render(){
  return (
    <div className="App">
      <Form value={this.state.value} 
      change={this.handleInputChange}
      submit={this.handleCitySubmit}/>
      <Result />
    </div>
  );
}
}

export default App;
