import React, { useState } from 'react';
import Clear from "./image/Clear.jpg";
import Clouds from "./image/Clouds.jpg";
import Drizzle from "./image/Drizzle.jpg";
import Mist from "./image/Mist.jpg";
import Rain from "./image/Rain.jpg";
import Polana from "./image/polana.jpg";

const api = {
  key: "3b5cff616604c3fac74ed40311ad2ce2",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {
  const [query, setQuery] = useState('');
  const [data, setData] = useState({});
  const [forecast, setForecast] = useState([]);

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setData(result);
          setQuery('');
          console.log(data.weather);
        });
      fetch(`${api.base}forecast?q=${query}&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
        setForecast(result.list);
        console.log(forecast)
        });
    }
  }

  const dateBuilder = (d) => {
    let months = ["Styczeń", "Luty", "Marzec", "Kwiecień", "Maj", "Czerwiec", "Lipiec", "Sierpień", "Wrzesień", "Październik", "Listopad", "Grudzień"];
    let days = ["Niedziela", "Poniedziałek", "Wtorek", "Środa", "Czwartek", "Piątek", "Sobota"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`
  }

  let backgroundImage = Polana;

  if (typeof data.weather !== "undefined") {
    switch (data.weather[0].main) {
      case "Clear":
        backgroundImage = Clear;
        break;
      case "Clouds":
        backgroundImage = Clouds;
        break;
      case "Drizzle":
        backgroundImage = Drizzle;
        break;
      case "Mist":
        backgroundImage = Mist;
        break;
      case "Rain":
        backgroundImage = Rain;
        break;
      default:
        backgroundImage = Polana;
    }
  }

  const weatherTranslations = {
    Clear: "Słonecznie",
    Clouds: "Pochmurnie",
    Drizzle: "Mżawka",
    Mist: "Mgła",
    Rain: "Deszcz"
  };

  return (
    <div className="app" style={{ backgroundImage: `url('${backgroundImage}')`, backgroundSize: "cover", backgroundPosition: "bottom" }}>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Wpisz miasto"
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {typeof data.main !== "undefined" ? (
          <div>
            <div className="location-box">
              <div className="location">{data.name}, {data.sys.country}</div>
              <div className="date">{dateBuilder(new Date())}</div>
            </div>
            <div className="weather-box">
              <div className="info">
                <div className='temp'>{Math.round(data.main.temp)}°C<br /></div>
                <div className="weather">{weatherTranslations[data.weather[0].main]} {Math.round(data.main.temp_max)}/{Math.round(forecast[0].main.temp)}°C</div>
              </div>
            </div>
          </div>
        ) : ''}
      </main>
    </div>
  )
}

export default App;
