import React, { useState } from 'react';
import Clear from "./image/Clear.png";
import Clouds from "./image/Clouds.png";
import Drizzle from "./image/Drizzle.png";
import Mist from "./image/Mist.png";
import Rain from "./image/Rain.png";
import Polana from "./image/polana.png";
import Thunderstorm from "./image/Thunderstorm.png";
import Snow from "./image/Snow.png";
const firebaseConfig = {
  apiKey: "AIzaSyCHJBRSJz4nt9FxlaF3L_dljwTnGXxIyYY",
  authDomain: "sm-weather-app-17d72.firebaseapp.com",
  projectId: "sm-weather-app-17d72",
  storageBucket: "sm-weather-app-17d72.appspot.com",
  messagingSenderId: "53388441210",
  appId: "1:53388441210:web:d181e24c4d0a2de6a4edc3"
};
const api = {
  key: "3b5cff616604c3fac74ed40311ad2ce2",
  base: "https://api.openweathermap.org/data/2.5/"
};

function App() {
  const [query, setQuery] = useState('');
  const [data, setData] = useState({});
  const [forecast, setForecast] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const search = (evt) => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          if (result.cod === "404") {
            setErrorMessage("Nie znaleziono miasta");
            setData({});
            setForecast([]);
          } else {
            setData(result);
            setForecast([]);
            setErrorMessage('');
          }
          setQuery('');
        });
      fetch(`${api.base}forecast?q=${query}&units=metric&APPID=${api.key}`)
        .then((res) => res.json())
        .then((result) => {
          setForecast(result.list);
        });
    }
  };

  const dateBuilder = (d) => {
    let months = [
      "Styczeń",
      "Luty",
      "Marzec",
      "Kwiecień",
      "Maj",
      "Czerwiec",
      "Lipiec",
      "Sierpień",
      "Wrzesień",
      "Październik",
      "Listopad",
      "Grudzień"
    ];
    let days = [
      "Niedziela",
      "Poniedziałek",
      "Wtorek",
      "Środa",
      "Czwartek",
      "Piątek",
      "Sobota"
    ];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`;
  };

  let backgroundImage = Polana;

  if (typeof data.weather !== "undefined") {
    switch (data.weather[0].main) {
      case "Clear":
        backgroundImage = Clear;
        break;
      case "Clouds":
        backgroundImage = Clouds;
        break;
      case "Drizzle":
        backgroundImage = Drizzle;
        break;
      case "Mist":
        backgroundImage = Mist;
        break;
      case "Rain":
        backgroundImage = Rain;
        break;
      case "Thunderstorm":
        backgroundImage = Thunderstorm;
        break;
      case "Snow":
        backgroundImage = Snow;
        break;
      default:
        backgroundImage = Polana;
    }
  }

  const weatherTranslations = {
    Clear: "Słonecznie",
    Clouds: "Pochmurnie",
    Drizzle: "Lekki deszcz",
    Mist: "Mgliście",
    Rain: "Deszczowo",
    Thunderstorm: "Burzowo",
    Snow: "Opady śniegu"
  };

  const getLowestTemperature = () => {
    if (forecast.length > 0) {
      return forecast.reduce((minTemp, item) => {
        if (item.main.temp < minTemp) {
          return item.main.temp;
        }
        return minTemp;
      }, Number.POSITIVE_INFINITY);
    }
    return '';
  };

  const formatTime = (time) => {
    const options = { hour: 'numeric', minute: 'numeric' };
    return new Date(time * 1000).toLocaleTimeString('pl-PL', options);
  };

  return (
    <div
      className="app"
      style={{
        backgroundImage: `url('${backgroundImage}')`,
        backgroundSize: "cover",
        backgroundPosition: "bottom"
      }}
    >
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Wpisz miasto"
            onChange={(e) => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {typeof data.main !== "undefined" ? (
          <div className="weather-box">
            <div className="info">
              <div className="location-box">
                <div className="location">{data.name}, {data.sys.country}</div>
                <div className="date">{dateBuilder(new Date())}</div>
              </div>
              <div className='temp'>{Math.round(data.main.temp)}°C<br /></div>
              <div className="weather">
                {weatherTranslations[data.weather[0].main]} {Math.round(data.main.temp_max)}/{Math.round(getLowestTemperature())}°C
              </div>
              <div className="pressure">{data.main.pressure} hPa</div>
              <div className="sun">Wschód {formatTime(data.sys.sunrise)}</div>
              <div className="sun">Zachód {formatTime(data.sys.sunset)}</div>
            </div>
          </div>
        ) : (
          <div className="message">
            <div className="error-message">{errorMessage}</div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
